// Ticket Form component
const TicketForm = ({ projectId, onTicketCreated, onClose }) => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [status, setStatus] = useState('To Do');
  const [priority, setPriority] = useState('Medium');
  const [assignee, setAssignee] = useState('');
  const [type, setType] = useState('Bug');
  const [dueDate, setDueDate] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [projectMembers, setProjectMembers] = useState([]); // For assignee dropdown

  // Fetch project members for assignee dropdown
  useEffect(() => {
    const fetchProjectMembers = async () => {
      try {
        const token = getToken();
        const config = { headers: { Authorization: `Bearer ${token}` } };
        const response = await axios.get(`${API_BASE_URL}/projects/${projectId}`, config);
        // Combine owner and team members, filter out duplicates if any
        const members = [response.data.owner, ...response.data.teamMembers]
            .filter((member, index, self) =>
                member && self.findIndex(m => m._id === member._id) === index
            );
        setProjectMembers(members);
      } catch (err) {
        console.error('Error fetching project members:', err);
        setMessage('Failed to load project members.');
      }
    };
    if (projectId) {
        fetchProjectMembers();
    }
  }, [projectId]);


  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      const token = getToken();
      if (!token) {
        setMessage('Not authenticated. Please log in.');
        setLoading(false);
        return;
      }

      const config = {
        headers: { Authorization: `Bearer ${token}` },
      };

      const newTicket = {
        project: projectId,
        title,
        description,
        status,
        priority,
        assignee: assignee || null, // Send null if no assignee selected
        type,
        dueDate: dueDate || null,
      };

      const response = await axios.post(`${API_BASE_URL}/tickets`, newTicket, config);
      setMessage('Ticket created successfully!');
      onTicketCreated(response.data); // Pass new ticket data to parent
      onClose(); // Close the modal
    } catch (error) {
      setMessage(error.response?.data?.message || 'Failed to create ticket');
      console.error('Ticket creation error:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <h2 className="text-2xl font-bold text-gray-800 mb-4">Create New Ticket</h2>
      {message && <p className={`mb-4 text-center ${message.includes('successfully') ? 'text-green-600' : 'text-red-600'}`}>{message}</p>}

      <div>
        <label className="block text-gray-700 text-sm font-bold mb-1" htmlFor="title">Title</label>
        <input
          type="text"
          id="title"
          className="w-full p-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
      </div>

      <div>
        <label className="block text-gray-700 text-sm font-bold mb-1" htmlFor="description">Description</label>
        <textarea
          id="description"
          rows="3"
          className="w-full p-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        ></textarea>
      </div>

      <div>
        <label className="block text-gray-700 text-sm font-bold mb-1" htmlFor="status">Status</label>
        <select
          id="status"
          className="w-full p-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
          value={status}
          onChange={(e) => setStatus(e.target.value)}
        >
          <option>To Do</option>
          <option>In Progress</option>
          <option>Done</option>
          <option>Blocked</option>
        </select>
      </div>

      <div>
        <label className="block text-gray-700 text-sm font-bold mb-1" htmlFor="priority">Priority</label>
        <select
          id="priority"
          className="w-full p-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
          value={priority}
          onChange={(e) => setPriority(e.target.value)}
        >
          <option>Low</option>
          <option>Medium</option>
          <option>High</option>
          <option>Critical</option>
        </select>
      </div>

      <div>
        <label className="block text-gray-700 text-sm font-bold mb-1" htmlFor="assignee">Assignee</label>
        <select
          id="assignee"
          className="w-full p-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
          value={assignee}
          onChange={(e) => setAssignee(e.target.value)}
        >
          <option value="">Unassigned</option>
          {projectMembers.map(member => (
            <option key={member._id} value={member._id}>{member.name} ({member.email})</option>
          ))}
        </select>
      </div>

      <div>
        <label className="block text-gray-700 text-sm font-bold mb-1" htmlFor="type">Type</label>
        <select
          id="type"
          className="w-full p-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
          value={type}
          onChange={(e) => setType(e.target.value)}
        >
          <option>Bug</option>
          <option>Feature</option>
          <option>Task</option>
        </select>
      </div>

      <div>
        <label className="block text-gray-700 text-sm font-bold mb-1" htmlFor="dueDate">Due Date</label>
        <input
          type="date"
          id="dueDate"
          className="w-full p-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
          value={dueDate}
          onChange={(e) => setDueDate(e.target.value)}
        />
      </div>

      <button
        type="submit"
        className="w-full bg-indigo-600 text-white py-2 px-4 rounded-lg hover:bg-indigo-700 transition duration-300 ease-in-out shadow-md"
        disabled={loading}
      >
        {loading ? 'Creating...' : 'Create Ticket'}
      </button>
    </form>
  );
};


// Component to display a list of tickets for a project
const TicketList = ({ tickets }) => {
  const getPriorityColor = (priority) => {
    switch (priority) {
      case 'High': return 'text-red-600';
      case 'Critical': return 'text-red-800 font-bold';
      case 'Medium': return 'text-yellow-600';
      case 'Low': return 'text-green-600';
      default: return 'text-gray-600';
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'To Do': return 'bg-gray-200 text-gray-800';
      case 'In Progress': return 'bg-blue-200 text-blue-800';
      case 'Done': return 'bg-green-200 text-green-800';
      case 'Blocked': return 'bg-red-200 text-red-800';
      default: return 'bg-gray-200 text-gray-800';
    }
  };

  if (!tickets || tickets.length === 0) {
    return <p className="text-center text-gray-500 italic mt-8">No tickets found for this project. Create one!</p>;
  }

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mt-6">
      {tickets.map(ticket => (
        <div key={ticket._id} className="bg-white p-4 rounded-lg shadow-sm border border-gray-200 hover:shadow-md transition duration-200 ease-in-out">
          <h4 className="font-semibold text-gray-800 mb-1">{ticket.title}</h4>
          <p className="text-sm text-gray-600 mb-2 truncate">{ticket.description || 'No description.'}</p>
          <div className="flex justify-between items-center text-xs text-gray-500 mb-2">
            <span>Reporter: {ticket.reporter?.name || 'N/A'}</span>
            <span>Assignee: {ticket.assignee?.name || 'Unassigned'}</span>
          </div>
          <div className="flex justify-between items-center text-sm">
            <span className={`px-2 py-1 rounded-full ${getStatusColor(ticket.status)}`}>{ticket.status}</span>
            <span className={`${getPriorityColor(ticket.priority)}`}>Priority: {ticket.priority}</span>
          </div>
          <div className="mt-3 flex justify-end space-x-2">
            <button className="text-indigo-600 hover:text-indigo-800 text-sm font-medium">View</button>
            <button className="text-gray-500 hover:text-gray-700 text-sm font-medium">Edit</button>
          </div>
        </div>
      ))}
    </div>
  );
};


// Component to display a single project's details and its tickets
const ProjectDetails = ({ projectId, onBackToProjects, allProjects, onSelectProject }) => {
  const [project, setProject] = useState(null);
  const [tickets, setTickets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [isTicketFormOpen, setIsTicketFormOpen] = useState(false);

  // Function to fetch project and tickets
  const fetchProjectAndTickets = useCallback(async () => {
    try {
      const token = getToken();
      if (!token) {
        setError('User not authenticated. Please log in.');
        setLoading(false);
        return;
      }
      const config = { headers: { Authorization: `Bearer ${token}` } };

      // Fetch project details
      const projectResponse = await axios.get(`${API_BASE_URL}/projects/${projectId}`, config);
      setProject(projectResponse.data);

      // Fetch tickets for this project
      const ticketsResponse = await axios.get(`${API_BASE_URL}/tickets/${projectId}`, config);
      setTickets(ticketsResponse.data);

    } catch (err) {
      setError(err.response?.data?.message || 'Failed to fetch project details or tickets');
      console.error('Error fetching project/tickets:', err);
    } finally {
      setLoading(false);
    }
  }, [projectId]); // Depend on projectId

  useEffect(() => {
    if (projectId) {
      fetchProjectAndTickets();
    }
  }, [projectId, fetchProjectAndTickets]); // Re-fetch if projectId or fetchProjectAndTickets changes


  const handleTicketCreated = (newTicket) => {
    // Add the newly created ticket to the list
    setTickets((prevTickets) => [...prevTickets, newTicket]);
    setIsTicketFormOpen(false); // Close the modal
  };

  if (loading) return <div className="text-center text-gray-500">Loading project...</div>;
  if (error) return <div className="text-center text-red-600">{error}</div>;
  if (!project) return <div className="text-center text-gray-500">Project not found.</div>;

  return (
    <div className="p-6">
      <div className="mb-4 flex items-center">
        <button
          onClick={onBackToProjects}
          className="text-gray-600 hover:text-indigo-600 mr-2 focus:outline-none"
          title="Back to Projects"
        >
          <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 19l-7-7m0 0l7-7m-7 7h18" />
          </svg>
        </button>
        <h2 className="text-3xl font-bold text-gray-800">{project.title}</h2>

        {/* Project Selector Dropdown */}
        <div className="ml-4 flex items-center">
            <label htmlFor="project-selector" className="sr-only">Select Project</label>
            <select
                id="project-selector"
                value={projectId}
                onChange={(e) => onSelectProject(e.target.value)}
                className="block w-auto py-2 px-3 border border-gray-300 bg-white rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
            >
                {allProjects.map((proj) => (
                    <option key={proj._id} value={proj._id}>
                        {proj.title}
                    </option>
                ))}
            </select>
        </div>
      </div>
      <p className="text-gray-600 text-lg mb-6">{project.description}</p>

      <div className="mb-8 p-4 bg-white rounded-lg shadow-md border border-gray-200">
        <h3 className="text-xl font-semibold text-gray-800 mb-4">Project Details</h3>
        <p className="text-gray-700 mb-2"><strong>Owner:</strong> {project.owner?.name || 'N/A'} ({project.owner?.email || ''})</p>
        <p className="text-gray-700 mb-2"><strong>Status:</strong> {project.status}</p>
        <div className="mb-2">
            <h4 className="font-medium text-gray-700">Team Members:</h4>
            {project.teamMembers.length > 0 ? (
                <ul className="list-disc list-inside text-gray-600 ml-4">
                    {project.teamMembers.map(member => (
                        <li key={member._id}>{member.name} ({member.email})</li>
                    ))}
                </ul>
            ) : (
                <p className="text-gray-500 italic ml-4">No team members added yet.</p>
            )}
        </div>
      </div>

      <div className="flex justify-between items-center mb-4">
        <h3 className="text-2xl font-bold text-gray-800">Tickets</h3>
        <button
          onClick={() => setIsTicketFormOpen(true)}
          className="bg-indigo-600 text-white px-4 py-2 rounded-lg hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-opacity-50 transition duration-300 ease-in-out shadow-md"
        >
          New Ticket
        </button>
      </div>

      <TicketList tickets={tickets} />

      <Modal isOpen={isTicketFormOpen} onClose={() => setIsTicketFormOpen(false)}>
        <TicketForm
          projectId={projectId}
          onTicketCreated={handleTicketCreated}
          onClose={() => setIsTicketFormOpen(false)}
        />
      </Modal>
    </div>
  );
};


const App = () => {
  const [currentPage, setCurrentPage] = useState('projects');
  const [selectedProjectId, setSelectedProjectId] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(!!getToken());
  const [projects, setProjects] = useState([]); // State for all projects
  const [projectsLoading, setProjectsLoading] = useState(true);
  const [projectsError, setProjectsError] = useState('');
  const currentUser = getCurrentUser(); // Get current user details

  // Fetch projects on authentication state change or when a new project is created
  const fetchProjects = useCallback(async () => {
    setProjectsLoading(true);
    setProjectsError('');
    try {
      const token = getToken();
      if (!token) {
        setProjectsError('User not authenticated. Please log in.');
        setProjectsLoading(false);
        return;
      }
      const config = {
        headers: { Authorization: `Bearer ${token}` },
      };
      const response = await axios.get(`${API_BASE_URL}/projects`, config);
      setProjects(response.data);
      if (response.data.length > 0 && !selectedProjectId) {
        // Automatically select the first project if none is selected
        setSelectedProjectId(response.data[0]._id);
      }
    } catch (err) {
      setProjectsError(err.response?.data?.message || 'Failed to fetch projects');
      console.error('Error fetching projects:', err);
      // If fetching fails, clear selected project as it might be invalid
      setSelectedProjectId(null);
    } finally {
      setProjectsLoading(false);
    }
  }, [selectedProjectId]); // Dependency on selectedProjectId to potentially re-fetch if it changes unexpectedly

  useEffect(() => {
    if (isAuthenticated) {
      fetchProjects();
    }
  }, [isAuthenticated, fetchProjects]); // Re-fetch projects when auth status changes or fetchProjects is updated


  const handleRegisterSuccess = () => {
    setIsAuthenticated(true);
    setCurrentPage('projects');
    fetchProjects(); // Fetch projects immediately after successful registration/login
  };

  const handleProjectCreated = (newProject) => {
    setProjects((prevProjects) => [...prevProjects, newProject]);
    setSelectedProjectId(newProject._id); // Auto-select the newly created project
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setIsAuthenticated(false);
    setProjects([]);
    setSelectedProjectId(null);
    setCurrentPage('projects'); // Go back to login/register page
  };


  const renderPage = () => {
    if (!isAuthenticated) {
        return <RegisterForm onRegisterSuccess={handleRegisterSuccess} />;
    }

    if (selectedProjectId) {
      return (
        <ProjectDetails
          projectId={selectedProjectId}
          onBackToProjects={() => setSelectedProjectId(null)}
          allProjects={projects} // Pass all projects for the selector
          onSelectProject={setSelectedProjectId} // Pass handler to change selected project
        />
      );
    }

    switch (currentPage) {
      case 'projects':
        return (
            <>
                <ProjectDashboard
                    onSelectProject={setSelectedProjectId}
                    onAddProject={() => setIsProjectFormOpen(true)} // Open modal for new project
                    projects={projects}
                    loading={projectsLoading}
                    error={projectsError}
                />
                <Modal isOpen={isProjectFormOpen} onClose={() => setIsProjectFormOpen(false)}>
                    <CreateProjectForm
                        onProjectCreated={handleProjectCreated}
                        onClose={() => setIsProjectFormOpen(false)}
                    />
                </Modal>
            </>
        );
      case 'dashboard':
        return <div className="p-6"><Header title="Kanban Board" onLogout={handleLogout} /><p className="text-center text-gray-500 mt-8">Kanban board coming soon!</p></div>;
      default:
        return (
            <>
                <ProjectDashboard
                    onSelectProject={setSelectedProjectId}
                    onAddProject={() => setIsProjectFormOpen(true)}
                    projects={projects}
                    loading={projectsLoading}
                    error={projectsError}
                />
                <Modal isOpen={isProjectFormOpen} onClose={() => setIsProjectFormOpen(false)}>
                    <CreateProjectForm
                        onProjectCreated={handleProjectCreated}
                        onClose={() => setIsProjectFormOpen(false)}
                    />
                </Modal>
            </>
        );
    }
  };

  const [isProjectFormOpen, setIsProjectFormOpen] = useState(false); // State for project creation modal

  return (
    <div className="font-sans antialiased text-gray-900 bg-gray-50 min-h-screen">
      <style>
        {`
          body {
            font-family: 'Inter', sans-serif;
            margin: 0;
            padding: 0;
            box-sizing: border-box;
          }
          ::-webkit-scrollbar {
            height: 8px;
            width: 8px;
          }
          ::-webkit-scrollbar-track {
            background: #f1f1f1;
            border-radius: 10px;
          }
          ::-webkit-scrollbar-thumb {
            background: #888;
            border-radius: 10px;
          }
          ::-webkit-scrollbar-thumb:hover {
            background: #555;
          }
        `}
      </style>

      <div className="flex">
        {isAuthenticated && (
            <Sidebar
                onNavigate={setCurrentPage}
                currentUserId={currentUser?._id}
                userEmail={currentUser?.email}
            />
        )}
        <main className="flex-1 p-4 overflow-auto">
          {isAuthenticated && <Header title={selectedProjectId ? project?.title : "Projects"} onAddProject={() => setIsProjectFormOpen(true)} onLogout={handleLogout} />}
          {renderPage()}
        </main>
      </div>
    </div>
  );
};

export default App;
