import React, { useState, useEffect, useCallback, useMemo } from 'react';
import axios from 'axios';
import {
  DndContext,
  closestCorners,
  KeyboardSensor,
  PointerSensor,
  useSensor,
  useSensors,
} from '@dnd-kit/core';
import {
  arrayMove,
  SortableContext,
  sortableKeyboardCoordinates,
  verticalListSortingStrategy,
} from '@dnd-kit/sortable';
import { useSortable } from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';

// Correct imports for Droppable functionality using the useDroppable hook
import { useDroppable } from '@dnd-kit/core';


// Base URL for your backend API
const API_BASE_URL = 'http://localhost:5001/api'; // Ensure this matches your backend port

// Helper function to get token from localStorage
const getToken = () => localStorage.getItem('token');

// Helper function to get current user details from localStorage
const getCurrentUser = () => {
    try {
        const user = localStorage.getItem('user');
        return user ? JSON.parse(user) : null;
    } catch (e) {
        console.error("Error parsing user from localStorage", e);
        return null;
    }
};

// Define a simple header component
const Header = ({ title, onAddProject, onLogout }) => {
