            <h3 className={`text-lg font-semibold text-white p-2 mb-4 rounded-lg ${status.color}`}>
              {status.name} ({dummyTickets[status.id]?.length || 0})
            </h3>
            <div className="space-y-4">
              {dummyTickets[status.id]?.length > 0 ? (
                dummyTickets[status.id].map(ticket => (
                  <div key={ticket.id} className="bg-white p-4 rounded-lg shadow-sm border border-gray-200 cursor-grab hover:shadow-md transition duration-200 ease-in-out">
                    <h4 className="font-medium text-gray-800 mb-1">{ticket.title}</h4>
                    <p className="text-sm text-gray-600 mb-2">Assignee: {ticket.assignee}</p>
                    <span className={`text-xs ${getPriorityColor(ticket.priority)}`}>
                      Priority: {ticket.priority}
                    </span>
                  </div>
                ))
              ) : (
                <p className="text-center text-gray-500 italic">No tickets in this column.</p>
              )}
              <button className="w-full bg-gray-200 text-gray-700 py-2 rounded-lg hover:bg-gray-300 transition duration-200 ease-in-out flex items-center justify-center">
